import React, { Component } from 'react';

export default class App extends Component {
  state = {
    count: 0,
  };
  modify = (n) => {
    this.setState({
      count: n,
    });
  };
  render() {
    const { count } = this.state;
    return (
      <div>
        {count}
        <button onClick={() => modify((count += 1))}>Increase</button>
      </div>
    );
  }
}
