import React, { Component, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function useInput(defaultValue) {
  const [Value, setValue] = useState(defaultValue);

  const onChange = (e) => {
    const {
      target: { Value },
    } = e;
    setValue(Value);
  };

  return { Value, onChange };
}

function App() {
  const name = useInput('');
  console.log(name);
  return (
    <div className='App'>
      <h1>USE HOOKS!!</h1>
      <input {...name} placeholder="What's your name?" />
    </div>
  );
}

export default App;
